package com.opencn.mesh.model;import lombok.Builder;import java.io.Serializable;@Builderpublic class PipelineResponse<T> implements Serializable {    private boolean success = true;    private String  error   = "";    private T       data;    public boolean isSuccess() {        return success;    }    public void setSuccess(boolean success) {        this.success = success;    }    public String getError() {        return error;    }    public void setError(String error) {        this.error = error;    }    public T getData() {        return data;    }    public void setData(T data) {        this.data = data;    }}