package com.opencn.mesh;import lombok.Data;import lombok.Getter;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.context.annotation.Configuration;import java.util.List;import java.util.Map;/** * { *   "instance01": [ *     { *       "name": "ctn-tracking", *       "version": "1.0.0", *       "repo": "http://10.10.10.40/public_share/ctn-tracking-1.0.0-ark-biz.jar", *       "status": 0 *     } *   ], *   "instance02": [ *      { *         "name": "latbox-plugin", *         "version": "1.0.0", *         "repo": "http://10.10.10.40/public_share/latbox-plugin-1.0.0-ark-biz.jar", *         "status": 0 *       } *   ] * } * * */@Configuration@Data@Getter@ConfigurationProperties(prefix = "pipeline-config")public class PipelineApolloConfig {    static String CONFIG_META_PLUGINS = "plugins";    private Map<String, InstanceConfig> instances;    @Data    @Getter    static class InstanceConfig {        private List<PluginConfig> plugins;    }    @Data    @Getter    static class PluginConfig {        private String name;        private String version;        private String repo;        private int status;        private int priority;    }}