package com.opencn.mesh;import com.ctrip.framework.apollo.openapi.client.ApolloOpenApiClient;import lombok.Data;import lombok.Getter;import lombok.Setter;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configuration@Getter@Setter@Datapublic class PipelineApolloEnvConfig {    @Value("${spring.application.name}")    private String appId;    @Value("${appkey.apollo.env}")    private String env;    @Value("${appkey.apollo.cluster}")    private String cluster;    @Value("${appkey.apollo.url}")    private String portalUrl;    @Value("${appkey.apollo.token}")    private String token;    @Bean    public ApolloOpenApiClient apolloOpenApiClient() {        return ApolloOpenApiClient.newBuilder()                .withPortalUrl(portalUrl)                .withToken(token)                .build();    }}