package com.opencn.mesh.service;import com.opencn.mesh.BizMappingRegister;import com.opencn.mesh.model.PipelineRequest;import com.opencn.mesh.model.PipelineResponse;public interface BizCommonService {    default PipelineResponse invoke(PipelineRequest request) {        return  BizMappingRegister.execute(request.getBizKey(), request.getBizData());    }    default PipelineResponse subscribe(PipelineRequest request) {        return  BizMappingRegister.execute(request.getBizKey(), request.getBizData());    }    default PipelineResponse callback(PipelineRequest request) {        return  BizMappingRegister.execute(request.getBizKey(), request.getBizData());    }}